import groovy.json.JsonSlurper
class LIB_GeneralXMLs {
//XMLs def pointOfSale_XML = """<PointOfSale> <Location> <CountryCode>[MARKET]</CountryCode> </Location> </PointOfSale>"""
def party_XML = """ <Party> [SENDER] [PARTICIPANTS] </Party>"""
def party_IATA_XML = """ <IATA_Number>[SELLER]</IATA_Number> <AgencyID>[SELLERID]</AgencyID>"""
def party_NOIATA_XML = """ <OtherIDs> <OtherID>[SELLER]</OtherID> </OtherIDs> <AgencyID>[SELLERID]</AgencyID>"""
def party_senderWithCorp_XML = """ <Sender> <CorporateSender> <ID>[CORPORATE]</ID> </CorporateSender> </Sender>"""
def party_senderWithoutCorp_XML = """ <Sender> <TravelAgencySender> [IATAorNOIATA] </TravelAgencySender> </Sender>"""
def party_participants_XML = """ <Participants> [PARTICIPANTwithCORP] [PARTICIPANT] </Participants>"""
def participants_participant_XML = """ <Participant> <AggregatorParticipant SequenceNumber="[INDEX]"> <AggregatorID>[PARTICIPANT]</AggregatorID> </AggregatorParticipant> </Participant>"""
def participants_participantWithCorp_XML = """ <Participant> <TravelAgencyParticipant SequenceNumber="1"> <Name>AgencyName</Name> <Contacts> <Contact> <EmailContact> <Address>AgencyMail@xxx.com</Address> </EmailContact> <PhoneContact> <Application>BUSINESS</Application> <Number>+34666554433</Number> </PhoneContact> <Name> <Surname>Martínez</Surname> <Given>Pepe</Given> </Name> </Contact> </Contacts> [IATAorNOIATA] </TravelAgencyParticipant> </Participant>"""
def public testRunner, context, logger
def LIB_GeneralXMLs ( whichTestRunner, whichContext, whichLogger ){ this.logger = whichLogger this.context = whichContext this.testRunner = whichTestRunner }
//Devuelve XML de PointOfSale def getPointOfSale_XML(market) { logger.info ':: RUNNING -> //Libs/Lib_General XMLs/getPointOfSale_XML'
pointOfSale_XML = pointOfSale_XML.replace('[MARKET]', market) return pointOfSale_XML }
//Devuelve XML del Party def getParty_XML( seller, sellerID, lstParticipants, corporate, lst_IATAorNOIATA ) { logger.info ':: RUNNING -> //Libs/Lib_General XMLs/getParty_XML'
def json = new JsonSlurper() lst_IATAorNOIATA = json.parseText(lst_IATAorNOIATA) def IorN = lst_IATAorNOIATA.find() { it.key.equals(seller) } assert (IorN != null), "WARNING: el seller indicado no se encuentra en la lista lst_IATAorNOIATA que se encuentra en Libs/LST_IatasYApikeys" IorN = IorN.value lstParticipants = lstParticipants.split(',')
// IATA o NO IATA if ( 'I' == IorN ) { party_IATA_XML = party_IATA_XML.replace( '[SELLER]' , seller ) .replace( '[SELLERID]' , sellerID ) } else { // 'N' == IorN party_IATA_XML = party_NOIATA_XML.replace( '[SELLER]' , seller ) .replace( '[SELLERID]' , sellerID ) }
// PARTICIPANTS - CON o SIN CORPORATE if ( 'NO' == lstParticipants[0] && 'NO' == corporate.toUpperCase() ) {
participants_participant_XML = '' party_participants_XML = ''
} else if ( 'NO' != lstParticipants[0] && 'NO' == corporate.toUpperCase() ) {
def aux = '' lstParticipants.eachWithIndex() { item, indx -> indx = indx.toInteger() + 1 aux = aux + participants_participant_XML.replace( '[INDEX]' , indx.toString() ) .replace( '[PARTICIPANT]' , item ) }
party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , '' ) .replace( '[PARTICIPANT]' , aux )
} else if ( 'NO' != lstParticipants[0] && 'NO' != corporate.toUpperCase() ) {
def aux = '' def participantWithCorp = ''
participantWithCorp = participants_participantWithCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML )
lstParticipants.eachWithIndex() { item, indx -> indx = indx.toInteger() + 2 aux = aux + participants_participant_XML.replace( '[INDEX]' , indx.toString() ) .replace( '[PARTICIPANT]' , item ) } party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , participantWithCorp ) .replace( '[PARTICIPANT]' , aux )
} else { // 'NO' == lstParticipants[0] && 'NO' != corporate
def participantWithCorp = ''
participantWithCorp = participants_participantWithCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML ) party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , participantWithCorp ) .replace( '[PARTICIPANT]' , '' )
}
// CONSTRUCCION FINAL DEL PARTY if ( 'NO' == corporate.toUpperCase() ) {
party_senderWithoutCorp_XML = party_senderWithoutCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML ) party_XML = party_XML.replace( '[SENDER]' , party_senderWithoutCorp_XML ) .replace( '[PARTICIPANTS]' , party_participants_XML ) } else { // 'NO' != corporate party_senderWithCorp_XML = party_senderWithCorp_XML.replace( '[CORPORATE]' , corporate ) party_XML = party_XML.replace( '[SENDER]' , party_senderWithCorp_XML ) .replace( '[PARTICIPANTS]' , party_participants_XML ) }
return party_XML } }
final objName = "cntx_GXML" context.setProperty( objName, new LIB_GeneralXMLs ( testRunner, context, log) )

