import groovy.json.JsonSlurper

class LIB_OfferPriceXMLs {

	//XMLs
	def query_XML		= """<Query>
							<Offer OfferID="[OFFERID]" Owner="IB" ResponseID="[RESPONSEID]">
								[OFFERITEMS]
							</Offer>
						</Query>"""

	def offerItem_XML	= """<OfferItem OfferItemID="[OFFERITEM]">
							<PassengerRefs>[TAGS]</PassengerRefs>
						</OfferItem>"""

	def qualifier_XML	= """<Qualifier>
				            <SpecialNeedQualifiers>
				               <Code>DISA</Code>
				            </SpecialNeedQualifiers>
				         </Qualifier>"""


	def public testRunner, context, logger

	def LIB_OfferPriceXMLs ( whichTestRunner, whichContext, whichLogger ){
		this.logger	= whichLogger
		this.context	= whichContext
		this.testRunner = whichTestRunner
	}

		//Devuelve XML de Query
	def getQuery_XML( 	numADT, numCHD, numINF, responseID,
					offerItemID_ADT, offerItemID_CHD, offerItemID_INF, offerID ) {
		logger.info ':: RUNNING -> //Libs/LIB_OfferPrice XMLs/getQuery_XML'

		def tags 		= ''
		def offerItems	= ''

		for ( int i=0; i < numADT.toInteger(); i++ ){
			tags	= tags + "ADULT_0" + (i+1)
			if ( i != (numADT.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_ADT != ""  && offerItemID_ADT != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_ADT).replace("[TAGS]", tags)
		}

		tags = ""
		for ( int i=0; i < numCHD.toInteger(); i++ ){
			tags	= tags + "CHILD_0" + (i+1)
			if ( i != (numCHD.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_CHD != "" && offerItemID_CHD != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_CHD).replace("[TAGS]", tags)
		}

		tags = ""
		for ( int i=0; i < numINF.toInteger(); i++ ){
			tags	= tags + "INFANT_0" + (i+1)
			if ( i != (numINF.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_INF != "" && offerItemID_INF != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_INF).replace("[TAGS]", tags)
		}

		query_XML	= query_XML.replace("[OFFERID]"	, offerID		)
						 .replace("[RESPONSEID]"	, responseID	)
						 .replace("[OFFERITEMS]"	, offerItems	)		

		return query_XML
	}	

		//Devuelve XML de CoreQuery
	def getQualifier_XML( specialNeeds ) {
		logger.info ':: RUNNING -> //Libs/LIB_OfferPrice XMLs/getQualifier_XML'

		if( '' == specialNeeds || 'NO' == specialNeeds ) {
			qualifier_XML	= ''
		} // else manda el XML tal cual

		return qualifier_XML
	}
}

final objName	= "cntx_OPXML"
context.setProperty( objName, new LIB_OfferPriceXMLs ( testRunner, context, log) )

