con:config.script:
/*
 * Métodos:
 * 		- getPreviousStepName
 * 		- getDates
 * 		- removeProperties
 * 
 */

class LIB_SeveralFunctions {

	def public testRunner, context, logger
	def public currentStepInd, previousStep

	def LIB_SeveralFunctions ( whichTestRunner, whichContext, whichLogger ){
		this.logger	= whichLogger
		this.context	= whichContext
		this.testRunner = whichTestRunner
	}

	def getPreviousStepName() {
		logger.info ':: RUNNING -> //Libs/Lib_Several Functions/getPreviousStepName'

		currentStepInd = context.currentStepIndex
		previousStep = testRunner.testCase.getTestStepAt(currentStepInd - 1)
		return previousStep.name
	}

	def getDates( numSegments, date ) {
		logger.info ':: RUNNING -> //Libs/Lib_Several Functions/getDates'

		def arrayDates	= []
		def newDate	= new Date()
		def random	= new Random()
		def nDate		= ''

		switch (date.toUpperCase()) {
			case ('HOY'):
				nDate	= newDate.format("yyyy-MM-dd")
				arrayDates << nDate
				for ( int i = 1; i < numSegments; i++ ) {
					newDate 	= newDate + 15 + random.nextInt(20)
					nDate	= newDate.format("yyyy-MM-dd")
					arrayDates << nDate
				}
				break
			case ('MAÑANA'):
				newDate	= newDate + 1
				nDate	= newDate.format("yyyy-MM-dd")
				arrayDates << nDate
				for ( int i = 1; i < numSegments; i++ ) {
					newDate 	= newDate + 15 + random.nextInt(20)
					nDate	= newDate.format("yyyy-MM-dd")
					arrayDates << nDate
				}
				break
			case ['RANDOM','']:
				newDate	= newDate + 15 + random.nextInt(220)
				nDate	= newDate.format("yyyy-MM-dd")
				arrayDates << nDate
				for ( int i = 1; i < numSegments; i++ ) {
					newDate 	= newDate + 7 + random.nextInt(20)
					nDate	= newDate.format("yyyy-MM-dd")
					arrayDates << nDate
				}
				break
			case ['STOPOVER']:
				newDate	= newDate + 10 + random.nextInt(340)
				nDate	= newDate.format("yyyy-MM-dd")
				arrayDates << nDate
				for ( int i = 1; i < numSegments; i++ ) {
					newDate 	= newDate + 3
					nDate	= newDate.format("yyyy-MM-dd")
					arrayDates << nDate
				}
			break
			default:
				date 	= date.split(",")
				assert date.size() == numSegments, "ERROR: el número de fechas no se corresponde con el número de segmentos. Fechas -> ${date.size()} / Segmentos -> ${numSegments}"
				date.each() { d ->
					newDate = Date.parse("yyyyMMdd",d).format('yyyy-MM-dd')
					arrayDates << newDate
				}
		}
		return arrayDates
	}

	def removeProperties () {
		logger.info ':: RUNNING -> //Libs/Lib_Several Functions/removeProperties'

		for( prop in context.testCase.testSuite.project.properties) {
		   context.testCase.testSuite.project.removeProperty( prop.value.name )
		}
	}
}

final objName	= "cntx_SF"
context.setProperty( objName, new LIB_SeveralFunctions ( testRunner, context, log) )

/*
 * Métodos:
 * 		- getApikey
 * 		- setEndpoints
 * 		- enableSteps 
 * 
 */

import groovy.json.JsonSlurper

class Lib_SettingUpData {

	def public testRunner, context, logger

	def Lib_SettingUpData ( whichTestRunner, whichContext, whichLogger ){
		this.logger	 = whichLogger
		this.context	 = whichContext
		this.testRunner = whichTestRunner
	}

		//Devuelve la apikey según el entorno, sellers y agregadores
	def getApikey( environment, seller, participants, force_apikey,  lst_masheryUserName, lst_apiKey ) {
		logger.info ':: RUNNING -> //Libs/LIB_Setting up data/getApikey'

		def apikey 	= ' NO apikey'
		def json 		= new JsonSlurper()
		def masheryUserName, number

		if ( !force_apikey.equals('NO') && !force_apikey.equals('') ) {
			apikey	= force_apikey
			return apikey
		} else if ( !participants.equals('NO') && !participants.equals('') ) {
			participants	= participants.split(',')
			number		= participants[participants.size() - 1]
		} else {
			number		= seller	
		}

		lst_masheryUserName	= json.parseText(lst_masheryUserName)
		lst_apiKey		= json.parseText(lst_apiKey)
		masheryUserName	= lst_masheryUserName.find() { it.key.equals(number) }
		assert masheryUserName != null, "WARNING: es probable que no exista el seller $number en la lista lst_masheryUserName_$environment" 
		apikey			= lst_apiKey.find() { it.key.equals(masheryUserName.value) }

		return apikey.value
	}

		//Agrega a los parámetros los endpoints correspondientes a los servicios según el entorno y versión
	def setEndpoints( environment, lst_endpoints ) {
		logger.info ':: RUNNING -> //Libs/LIB_Setting up data/setEndpoints'

		def key		= ''
		def json 		= new JsonSlurper()		
		def endpoints	= json.parseText(lst_endpoints)

		endpoints.each() { 
			key	= it.key
			it 	= it.value.toString().replace('[ENVIRONMENT]', environment.toLowerCase() )
			testRunner.testCase.setPropertyValue( key, it.value.toString() )
			//logger.info " Service: $key. Endpoint: ${it.value} "
		}		
	}

		//Habilita y deshabilita los servicios/pasos del test
	def enableSteps( version, seatspre, bagspre, flightPriceDirect ) {
		logger.info ':: RUNNING -> //Libs/LIB_Setting up data/enableSteps'

		def services	=	[	  'AirShopping'		: false
							, 'FlightPrice'		: false
							, 'OfferPrice'			: false
							, 'Ancillaries Presale'	: true
							, 'Ancillaries Presale - Property Transfer'	: true
						]

		if ( '17'	== version ) {
			if ( 'YES' == flightPriceDirect.toUpperCase() ) { 
				services.AirShopping	= true
			} else {
				services.AirShopping	= false
			}

			bagspre.split(",").each() {
				if ( it != "NO" ) {
					services.('Ancillaries Presale')	= false
					services.('Ancillaries Presale - Property Transfer')	= false
				}
			}
		} else if ( '16' == version ) {
			if ( 'YES' == flightPriceDirect.toUpperCase() ) { 
				services.AirShopping	= true
			} else {
				services.AirShopping	= false
			}

			bagspre.split(",").each() {
				if ( it != "NO" ) {
					services.('Ancillaries Presale')	= false
					services.('Ancillaries Presale - Property Transfer')	= false
				}
			}			
		}

		seatspre.split(",").each() {
			if ( it != "NO" ) {
				services.('Ancillaries Presale')	= false
				services.('Ancillaries Presale - Property Transfer')	= false
			}
		}

		services.each() {
			try {
				testRunner.testCase.getTestStepByName( it.key ).disabled			= it.value
			} catch (Exception e) {
				logger.warn " WARNING: en este test no se encuentra el servicio/paso ${it.key}"
			}
		}
	}
}

final objName	= "cntx_SUD"
context.setProperty( objName, new Lib_SettingUpData( testRunner, context, log) )

/*
 * Métodos:
 * 		- getValidOffers_Carriers //Utilizado en los filtros filter_marketingCarrier y filter_operatingCarrier
 * 		- filter_numScales
 * 		- filter_priceClass
 * 		- filter_marketingCarrier
 * 		- filter_operatingCarrier
 * 		- filter_flightNum
 * 		- filter_airlinesMarketingCarrier
 * 		- filter_airlinesOperatingCarrier
 * 		- filter_beforeTime
 * 		- filter_afterTime
 * 		- getRandomOffer
 * 		- getOffer 
 * 		- getResponse
 */

import groovy.json.JsonSlurper

class Lib_GettingData {

	def public testRunner, context, logger

	def Lib_GettingData ( whichTestRunner, whichContext, whichLogger ){
		this.logger	 = whichLogger
		this.context	 = whichContext
		this.testRunner = whichTestRunner
	}

		//Método que se usa en los filtros de MarketingCarrier y OperatingCarrier
	def getValidOffers_Carriers(allOffers, flightSegmentsValid) {
		logger.info ':: RUNNING -> ../../getValidOffers_Carriers'

		def isValid		= true
		def segmentsRefs	= ''
		def validOffers	= []

		allOffers.each() { offer ->
				isValid = true
				offer.OfferItem[0].FareDetail.FareComponent.each() { fareComponent ->
					segmentsRefs	= fareComponent.SegmentRefs.toString().split(' ')
					segmentsRefs.each() {
						if ( !flightSegmentsValid.toString().contains(it) ) {
							isValid = false
						}
					}
				}
				if ( isValid ) { 
					validOffers << offer 
				}
			}

		return validOffers
	}


		//Devuelve las ofertas según la propiedad 'force_numScales'
	def filter_numScales( allOffers, nS, rs, numSegments ){
		logger.info ':: RUNNING -> ../filter_numScales'

		def validOffers	= []
		def segmentsRefs	= ''
		def isValid		= true
		nS	= nS.split(',')

		if ( ('NO' == nS[0] || 'RANDOM' == nS[0] || '' == nS[0]) && 2 > nS.size() ) {
			validOffers	= allOffers
		} else {
			assert nS.size() == numSegments, " ERROR: en la propiedad force_numScales se deben dar tantos valores como segmentos haya" 

			allOffers.each() { offer ->
				isValid	= true		
				offer.OfferItem[0].FareDetail.FareComponent.eachWithIndex() { fareComponent, index ->
					segmentsRefs	= fareComponent.SegmentRefs.toString().split(' ')
					if ( segmentsRefs.size() != nS[index].toInteger() ) {
						isValid	= false 
					}
				}
				if ( isValid ) { 
					validOffers << offer 
				}
			}
		}
		assert validOffers.size() > 0, " WARNING: no hay ofertas con esa configuración de escalas"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'priceClass'
	def filter_priceClass( allOffers, pC, rs ){
		logger.info ':: RUNNING -> ../filter_priceClass'		

		def validOffers	= []
		def segmentsRefs	= ''
		def priceClass		= ''
		def isValid		= true

		if ( 'NO' == pC || 'RANDOM' == pC || '' == pC ) {
			validOffers	= allOffers
		} else {
			isValid	= true
			allOffers.each() { offer ->	
				isValid	= true		
				offer.FlightsOverview.FlightRef.each(){
					if ( !pC.contains(it.@PriceClassRef.toString()) ) {
						isValid = false
					}
				}
				if ( isValid ) { 
					validOffers << offer 
				}
			}
		}
		assert validOffers.size() > 0, " ERROR: no hay ofertas de $pC"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'marketingCarrier'
	def filter_marketingCarrier( allOffers, mC, rs ){
		logger.info ':: RUNNING -> ../filter_marketingCarrier'

		def validOffers		= []
		def flightSegmentsValid = []

		if ( 'NO' == mC || 'RANDOM' == mC || '' == mC ) {
			validOffers	= allOffers
		} else {
			rs.DataLists.FlightSegmentList.FlightSegment.each() {
				if ( mC.contains(it.MarketingCarrier.AirlineID.toString()) ) {
					flightSegmentsValid << it.@SegmentKey.toString()
				}
			}

			validOffers	= getValidOffers_Carriers(allOffers, flightSegmentsValid)
		}

		assert validOffers.size() > 0, " WARNING: no hay ofertas para todos los segmentos con marketingCarrier -> $mC"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'operatingCarrier'
	def filter_operatingCarrier( allOffers, oC, rs ){
		logger.info ':: RUNNING -> ../filter_operatingCarrier'

		def validOffers		= []
		def segmentsRefs		= ''
		def isValid			= true
		def flightSegmentsValid = []

		if ( 'NO' == oC || 'RANDOM' == oC || '' == oC ) {
			validOffers	= allOffers
		} else {
			rs.DataLists.FlightSegmentList.FlightSegment.each() {
				if ( oC.contains(it.OperatingCarrier.AirlineID.toString()) ) {
					flightSegmentsValid << it.@SegmentKey.toString()
				}
			}

			validOffers	= getValidOffers_Carriers(allOffers, flightSegmentsValid)
		}

		assert validOffers.size() > 0, " WARNING: no hay ofertas para todos los segmentos con operatingCarrier -> $oC"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'flightNum'
			//Se tiene en cuenta las escalas. 
			//Solo comprueba que los números de vuelo dados estén dentro del trayecto pudiendo haber más números
	def filter_flightNum( allOffers, fN, rs ){
		logger.info ':: RUNNING -> ../filter_flightNum'

		def validOffers		= []
		def segmentsRefs		= ''
		def isValid			= true
		def flightSegments		= []
		def segmentsRef		= []

		if ( 'NO' == fN || 'RANDOM' == fN || '' == fN ) {
			validOffers	= allOffers
		} else {
			fN	= fN.split(',')
			fN.each() { num ->
				flightSegments << rs.DataLists.FlightSegmentList.FlightSegment.find() {
					it.MarketingCarrier.FlightNumber.toString().equals(num)
				}.@SegmentKey.toString()
				assert flightSegments != null, "ERROR: no hay segmento con número de vuelo $num"	
			}

			allOffers.each() { offer ->
				segmentsRef	= []
				isValid		= true
				offer.OfferItem.FareDetail.FareComponent.each() {
					segmentsRef << it.SegmentRefs
				}				

				flightSegments.each(){ flight ->
					if ( !segmentsRef.toString().contains(flight.toString()) ) {
						isValid = false
					}
				}

				if ( isValid ) { 
					validOffers << offer 
				}
			}
		}

		assert validOffers.size() > 0, " WARNING: no hay ofertas para todos los segmentos con operatingCarrier -> $fN"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'airlinesMarketingCarrier'
			//Todos los segmentos serán de una de las propiedades indicadas
	def filter_airlinesMarketingCarrier( allOffers, aMC, rs ){
		logger.info ':: RUNNING -> ../filter_airlinesMarketingCarrier'

		def validOffers		= []
		def isValid			= true
		def flightSegment		= []
		def segmentsRefLst		= []

		if ( 'NO' == aMC || 'RANDOM' == aMC || '' == aMC ) {
			validOffers	= allOffers
		} else {

			allOffers.each() { offer ->
				segmentsRefLst	= []
				isValid		= true
				offer.OfferItem[0].FareDetail.FareComponent.each() {
					segmentsRefLst << it.SegmentRefs
					segmentsRefLst	= segmentsRefLst.toString().replace('[', '').replace(']', '')
					segmentsRefLst.split(' ').each() { segment ->
						rs.DataLists.FlightSegmentList.FlightSegment.each() { flightSeg ->
							if ( flightSeg.@SegmentKey.toString().equals(segment.toString()) ) {
								if ( !aMC.toString().contains(flightSeg.MarketingCarrier.AirlineID.toString() ) ) {
									isValid = false
								}
							}
						}
					}
				}
				if ( isValid ) { 
					validOffers << offer
				}
			}
		}

		assert validOffers.size() > 0, " WARNING: no hay ofertas en la que todos los segmentos sea una de las siguientes aerolíneas con marketing carrier -> $aMC"
		return validOffers
	}

		//Devuelve las ofertas según la propiedad 'airlinesOperatingCarrier'
			//Todos los segmentos serán de una de las propiedades indicadas
	def filter_airlinesOperatingCarrier( allOffers, aOC, rs ){
		logger.info ':: RUNNING -> ../filter_airlinesOperatingCarrier'

		def validOffers		= []
		def isValid			= true
		def flightSegment		= []
		def segmentsRefLst		= []

		if ( 'NO' == aOC || 'RANDOM' == aOC || '' == aOC) {
			validOffers	= allOffers
		} else {

			allOffers.each() { offer ->
				segmentsRefLst	= []
				isValid		= true
				offer.OfferItem[0].FareDetail.FareComponent.each() {
					segmentsRefLst << it.SegmentRefs
					segmentsRefLst	= segmentsRefLst.toString().replace('[', '').replace(']', '')
					segmentsRefLst.split(' ').each() { segment ->
						rs.DataLists.FlightSegmentList.FlightSegment.each() { flightSeg ->
							if ( flightSeg.@SegmentKey.toString().equals(segment.toString()) ) {
								if ( !aOC.toString().contains(flightSeg.OperatingCarrier.AirlineID.toString() ) ) {
									isValid = false
								}
							}
						}
					}
				}
				if ( isValid ) { 
					validOffers << offer
				}
			}
		}

		assert validOffers.size() > 0, " WARNING: no hay ofertas en la que todos los segmentos sea una de las siguientes aerolíneas con operating carrier -> $aOC"
		return validOffers
	}

	        	//Devuelve ofertas en las que el primer segmento salga antes de la hora indicada
     def filter_beforeTime( allOffers, time, rs ) {
		logger.info ':: RUNNING -> ../filter_berofeTime'

          def validOffers   = []
		def isValid		= true
		def refs		= []
		def ref			= ''
		def flightSegment	= []

       	if ( 'NO' == time || 'RANDOM' == time || '' == time) {
			validOffers	= allOffers
		} else {

			if ( time.size() < 5 ) {
				time = '0' + time
			}			

	       	allOffers.each {
		      	isValid = true
	      		refs    = it.OfferItem[0].FareDetail.FareComponent[0].SegmentRefs.toString().split(' ')
		      	ref = refs[0]
		      	flightSegment   = rs.DataLists.FlightSegmentList.FlightSegment.find {
	               	it.@SegmentKey.toString().equals(ref)
	           	}

	           if ( flightSegment.Departure.Time.toString() > time ) {  isValid = false }
	           if ( isValid ) {    validOffers << it }
  			}	
		}	

       	assert validOffers.size() > 0, "ERROR: no hay ofertas con salida antes de la hora solicitada. Hora solicitada: $time H"
       	return validOffers
   	}

			//Devuelve ofertas en las que el primer segmento salga después de la hora indicada
     def filter_afterTime( allOffers, time, rs ) {
		  logger.info ':: RUNNING -> ../filter_afterTime'

       	def validOffers     = []
	  	def isValid		= true
	  	def refs		= []
	  	def ref			= ''
	  	def flightSegment	= []

       	if ( 'NO' == time || 'RANDOM' == time || '' == time) {
			validOffers	= allOffers
		} else {

			if ( time.size() < 5 ) {
				time = '0' + time
			}

       		allOffers.each {
           		isValid = true
           		refs    = it.OfferItem[0].FareDetail.FareComponent[0].SegmentRefs.toString().split(' ')
           		ref = refs[0]
          		flightSegment   = rs.DataLists.FlightSegmentList.FlightSegment.find {
                    	it.@SegmentKey.toString().equals(ref)
                	}
                if ( flightSegment.Departure.Time.toString() < time ) {  isValid = false }
                if ( isValid ) { validOffers << it }
       		}
		}

            assert validOffers.size() > 0, "ERROR: no hay ofertas con salida después de la hora solicitada. Hora solicitada: $time H"
            return validOffers
   	}

		//Devuelve una oferta de todas las ofertas válidas pasadas por parámetro
	def getRandomOffer( validOffers ) {
		logger.info ':: RUNNING -> ../getRandomOffer'
		def random  = new Random()
		def numRandom   = random.nextInt( validOffers.size())
		return validOffers[numRandom]
	}

		//Devuelve una oferta de todas las disponibles según los criterios
	def getOffer( 	numScales, priceClass, marketingCarrier,
				operatingCarrier, flightNum, airlinesMarketingCarrier, 
				airlinesOperatingCarrier, beforeTime, afterTime, rs, citiesLst ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data/getOffer'

		def offer			= ''
		def allOffers		= []
		def validOffers	= []
		def numSegments	= citiesLst.split(',').size() / 2

		allOffers 	= rs.OffersGroup.AirlineOffers.Offer
		validOffers	= filter_numScales( allOffers, numScales, rs, numSegments )
		validOffers	= filter_priceClass( validOffers, priceClass, rs )
		validOffers	= filter_marketingCarrier( validOffers, marketingCarrier, rs )
		validOffers	= filter_operatingCarrier( validOffers, operatingCarrier, rs )
		validOffers	= filter_flightNum( validOffers, flightNum, rs )
		validOffers	= filter_airlinesMarketingCarrier( validOffers, airlinesMarketingCarrier, rs )
		validOffers	= filter_airlinesOperatingCarrier( validOffers, airlinesOperatingCarrier, rs )
		validOffers	= filter_beforeTime( validOffers, beforeTime, rs )
		validOffers	= filter_afterTime( validOffers, afterTime, rs )

		offer		= getRandomOffer ( validOffers )

		return offer
	}

			//Devuelve el response
	def getResponse( rs ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data/getResponse'

		def response	= rs.ShoppingResponseID.ResponseID

		return response
	}
}

final objName	= "cntx_GO"
context.setProperty( objName, new Lib_GettingData( testRunner, context, log) )

/*
 *	Métodos:
 *		- setDataFromOfferID
 *		- gettingOfferItemsFromOfferPrice
 *		- getOfferID
 *		- getPNR
 *		- getTickets
 *		- getEMDS
 *		- getOfferItems
 */

import groovy.json.JsonSlurper

class Lib_GettingDataFromOfferID {

	def public testRunner, context, logger

	def Lib_GettingDataFromOfferID ( whichTestRunner, whichContext, whichLogger ){
		this.logger	 = whichLogger
		this.context	 = whichContext
		this.testRunner = whichTestRunner
	}

		//Método que se usa en los filtros de MarketingCarrier y OperatingCarrier
	def getDataFromOfferID ( offerID, numADTs, numCHDs, numINFs, rs ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/setDataFromOfferID'

		def offerItem	= ''
		def data	= 	[	'totalPriceOffer'		: 'N/A',
						'totalPriceToPay'		: 'N/A',
						'currency'			: 'N/A',
						'offerItemID_ADT_AirShopping'	: 'N/A',
						'offerItemID_CHD_AirShopping'	: 'N/A',
						'offerItemID_INF_AirShopping'	: 'N/A',
						'totalPriceADTS'		: 'N/A',
						'totalPriceCHDS'		: 'N/A',
						'totalPriceINFS'		: 'N/A',
						'totalPricePerADT'		: 'N/A',
						'totalPricePerCHD'		: 'N/A',
						'totalPricePerINF'		: 'N/A',
						'basePricePerADT'		: 'N/A',
						'basePricePerCHD'		: 'N/A',
						'basePricePerINF'		: 'N/A',
						'taxesPricePerADT'		: 'N/A',
						'taxesPricePerCHD'		: 'N/A',
						'taxesPricePerINF'		: 'N/A',
					]
		def priceClassLst	= []
		def segmentRefLst	= []
		def segmentLst		= []
		def flightNumLst	= []

		def offer = rs.OffersGroup.AirlineOffers.Offer.find() {
			it.@OfferID.toString().equals(offerID)
		}

		data.totalPriceOffer 	= offer.TotalPrice.SimpleCurrencyPrice
		data.totalPriceToPay	= data.totalPriceOffer
		data.currency			= offer.TotalPrice.SimpleCurrencyPrice.@Code.toString()

		offer.OfferItem[0].FareDetail.FareComponent.each() {
			priceClassLst	<< it.PriceClassRef
			segmentRefLst	<< it.SegmentRefs.toString().split(' ')
		}

		if ( numADTs != '0' ) {
			offerItem	= 	offer.OfferItem.find() {
							it.Service.PassengerRefs.toString().contains('ADULT')
						}
			data.offerItemID_ADT_AirShopping	= offerItem.@OfferItemID.toString()
			data.totalPriceADTS		= offerItem.TotalPriceDetail.TotalAmount.SimpleCurrencyPrice
			data.totalPricePerADT	= offerItem.FareDetail.Price.TotalAmount.SimpleCurrencyPrice
			data.basePricePerADT	= offerItem.FareDetail.Price.BaseAmount
			data.taxesPricePerADT	= offerItem.FareDetail.Price.Taxes.Total

		}

		if ( numCHDs != '0' ) {
			offerItem	= 	offer.OfferItem.find() {
							it.Service.PassengerRefs.toString().contains('CHILD')
						}
			data.offerItemID_CHD_AirShopping	= offerItem.@OfferItemID.toString()
			data.totalPriceCHDS		= offerItem.TotalPriceDetail.TotalAmount.SimpleCurrencyPrice
			data.totalPricePerCHD	= offerItem.FareDetail.Price.TotalAmount.SimpleCurrencyPrice
			data.basePricePerCHD	= offerItem.FareDetail.Price.BaseAmount
			data.taxesPricePerCHD	= offerItem.FareDetail.Price.Taxes.Total

		}

		if ( numINFs != '0' ) {
			offerItem	= 	offer.OfferItem.find() {
							it.Service.PassengerRefs.toString().contains('INFANT')
						}
			data.offerItemID_INF_AirShopping	= offerItem.@OfferItemID.toString()
			data.totalPriceINFS		= offerItem.TotalPriceDetail.TotalAmount.SimpleCurrencyPrice
			data.totalPricePerINF	= offerItem.FareDetail.Price.TotalAmount.SimpleCurrencyPrice
			data.basePricePerINF	= offerItem.FareDetail.Price.BaseAmount
			data.taxesPricePerINF	= offerItem.FareDetail.Price.Taxes.Total

		}			

		data.each(){
			testRunner.testCase.setPropertyValue( it.key.toString(), it.value.toString() )
		}

		def auxLst = segmentRefLst.toString() 	.replace(', '	, ',')
										.replace('['	, ''	)
										.replace(']'	, ''	)

		auxLst.split(',').each() {
			segmentLst	<< it.substring(0,14)
			flightNumLst	<< it.substring(2,6)
		}

		testRunner.testCase.setPropertyValue( 'priceClassLst', priceClassLst.toString().replace('[', '').replace(']','') )
		testRunner.testCase.setPropertyValue( 'segmentRefLst', segmentRefLst.toString().replace('[', '').replace(']','') )
		testRunner.testCase.setPropertyValue( 'segmentLst'   , segmentLst.toString().replace('[', '').replace(']','') )
		testRunner.testCase.setPropertyValue( 'flightNumLst'   , flightNumLst.toString().replace('[', '').replace(']','') )
	}

	def getSegmentLstV2	( rs ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getSegmentLstV2'

		def flightList			= rs.DataLists.FlightList
		def segmentReference	= []
		flightList.Flight.each {
			segmentReference	<< it.SegmentReferences.toString().split(' ')
		}

		return segmentReference.toString()
	}

	def getOfferItemsFromOfferPrice ( nADT, nCHD, nINF, rs ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getOfferItemsFromOfferPrice'

		def nItem 					= 0
		def offerItemID_ADT 	= ''
		def offerItemID_CHD 	= ''
		def offerItemID_INF 	= ''

		if ( nADT != '0' && nADT != 'NO' && nADT != '' ) {
			offerItemID_ADT	= rs.PricedOffer.OfferItem[nItem].@OfferItemID.toString()
			assert offerItemID_ADT != '', " ERROR: los offerItemsID desde el OfferPrice van vacíos. Comprobar respuesta de OfferPrice"
			nItem++
		} else {
			offerItemID_ADT	= 'N/A'
		}

		if ( nCHD != '0' && nCHD != 'NO' && nCHD != '' ) {
			offerItemID_CHD	= rs.PricedOffer.OfferItem[nItem].@OfferItemID.toString()
			assert offerItemID_CHD != '', " ERROR: los offerItemsID desde el OfferPrice van vacíos. Comprobar respuesta de OfferPrice"
			nItem++
		} else {
			offerItemID_CHD	= 'N/A'
		}

		if ( nINF != '0' && nINF != 'NO' && nINF != '' ) {
			offerItemID_INF	= rs.PricedOffer.OfferItem[nItem].@OfferItemID.toString()
			assert offerItemID_INF != '', " ERROR: los offerItemsID desde el OfferPrice van vacíos. Comprobar respuesta de OfferPrice"
		} else {
			offerItemID_INF	= 'N/A'
		}

		testRunner.testCase.setPropertyValue( 'offerItemID_ADT', offerItemID_ADT )
		testRunner.testCase.setPropertyValue( 'offerItemID_CHD', offerItemID_CHD )
		testRunner.testCase.setPropertyValue( 'offerItemID_INF', offerItemID_INF )

	}

	def getOfferID ( rs ) {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getOfferID'

		def offer = rs.PricedOffer.@OfferID.toString()
		return offer
	}

	def getPNR ( rs )  {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getPNR'

		def PNR = rs.Response.Order.BookingReferences.BookingReference.ID
		return PNR.toString()
	}

	def getTickets ( rs )  {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getTickets'

		def tickets	= []

		rs.Response.TicketDocInfos.TicketDocInfo.each(){
			if ( it.TicketDocument.Type.toString() == 'T' ) { 
				tickets << it.TicketDocument.TicketDocNbr 
			}
		}

		tickets	= tickets.toString().replace('[', '')
								.replace(']', '')

		if ( tickets == '' ) {
			tickets = 'N/A'
		}

		return tickets
	}

	def getEMDS ( rs )  {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getEMDS'

		def emds	= []

		rs.Response.TicketDocInfos.TicketDocInfo.each(){
			if ( it.TicketDocument.Type.toString() == 'J' ) { 
				emds << it.TicketDocument.TicketDocNbr 
			}
		}

		emds	= emds.toString().replace('[', '')
								.replace(']', '')

		if ( emds == '' ) {
			emds = 'N/A'
		}

		return emds
	}

	def getOfferItems ( rs )  {
		logger.info ':: RUNNING -> //Libs/LIB_Getting data from offer/getOfferItems'


		//return emds
	}	
}

final objName	= "cntx_GDFO"
context.setProperty( objName, new Lib_GettingDataFromOfferID( testRunner, context, log) )

/* * XML: * - getPointOfSale_XML * - getParty_XML */
import groovy.json.JsonSlurper
class LIB_GeneralXMLs {
//XMLs def pointOfSale_XML = """<PointOfSale> <Location> <CountryCode>[MARKET]</CountryCode> </Location> </PointOfSale>"""
def party_XML = """ <Party> [SENDER] [PARTICIPANTS] </Party>"""
def party_IATA_XML = """ <IATA_Number>[SELLER]</IATA_Number> <AgencyID>[SELLERID]</AgencyID>"""
def party_NOIATA_XML = """ <OtherIDs> <OtherID>[SELLER]</OtherID> </OtherIDs> <AgencyID>[SELLERID]</AgencyID>"""
def party_senderWithCorp_XML = """ <Sender> <CorporateSender> <ID>[CORPORATE]</ID> </CorporateSender> </Sender>"""
def party_senderWithoutCorp_XML = """ <Sender> <TravelAgencySender> [IATAorNOIATA] </TravelAgencySender> </Sender>"""
def party_participants_XML = """ <Participants> [PARTICIPANTwithCORP] [PARTICIPANT] </Participants>"""
def participants_participant_XML = """ <Participant> <AggregatorParticipant SequenceNumber="[INDEX]"> <AggregatorID>[PARTICIPANT]</AggregatorID> </AggregatorParticipant> </Participant>"""
def participants_participantWithCorp_XML = """ <Participant> <TravelAgencyParticipant SequenceNumber="1"> <Name>AgencyName</Name> <Contacts> <Contact> <EmailContact> <Address>AgencyMail@xxx.com</Address> </EmailContact> <PhoneContact> <Application>BUSINESS</Application> <Number>+34666554433</Number> </PhoneContact> <Name> <Surname>Martínez</Surname> <Given>Pepe</Given> </Name> </Contact> </Contacts> [IATAorNOIATA] </TravelAgencyParticipant> </Participant>"""
def public testRunner, context, logger
def LIB_GeneralXMLs ( whichTestRunner, whichContext, whichLogger ){ this.logger = whichLogger this.context = whichContext this.testRunner = whichTestRunner }
//Devuelve XML de PointOfSale def getPointOfSale_XML(market) { logger.info ':: RUNNING -> //Libs/Lib_General XMLs/getPointOfSale_XML'
pointOfSale_XML = pointOfSale_XML.replace('[MARKET]', market) return pointOfSale_XML }
//Devuelve XML del Party def getParty_XML( seller, sellerID, lstParticipants, corporate, lst_IATAorNOIATA ) { logger.info ':: RUNNING -> //Libs/Lib_General XMLs/getParty_XML'
def json = new JsonSlurper() lst_IATAorNOIATA = json.parseText(lst_IATAorNOIATA) def IorN = lst_IATAorNOIATA.find() { it.key.equals(seller) } assert (IorN != null), "WARNING: el seller indicado no se encuentra en la lista lst_IATAorNOIATA que se encuentra en Libs/LST_IatasYApikeys" IorN = IorN.value lstParticipants = lstParticipants.split(',')
// IATA o NO IATA if ( 'I' == IorN ) { party_IATA_XML = party_IATA_XML.replace( '[SELLER]' , seller ) .replace( '[SELLERID]' , sellerID ) } else { // 'N' == IorN party_IATA_XML = party_NOIATA_XML.replace( '[SELLER]' , seller ) .replace( '[SELLERID]' , sellerID ) }
// PARTICIPANTS - CON o SIN CORPORATE if ( 'NO' == lstParticipants[0] && 'NO' == corporate.toUpperCase() ) {
participants_participant_XML = '' party_participants_XML = ''
} else if ( 'NO' != lstParticipants[0] && 'NO' == corporate.toUpperCase() ) {
def aux = '' lstParticipants.eachWithIndex() { item, indx -> indx = indx.toInteger() + 1 aux = aux + participants_participant_XML.replace( '[INDEX]' , indx.toString() ) .replace( '[PARTICIPANT]' , item ) }
party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , '' ) .replace( '[PARTICIPANT]' , aux )
} else if ( 'NO' != lstParticipants[0] && 'NO' != corporate.toUpperCase() ) {
def aux = '' def participantWithCorp = ''
participantWithCorp = participants_participantWithCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML )
lstParticipants.eachWithIndex() { item, indx -> indx = indx.toInteger() + 2 aux = aux + participants_participant_XML.replace( '[INDEX]' , indx.toString() ) .replace( '[PARTICIPANT]' , item ) } party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , participantWithCorp ) .replace( '[PARTICIPANT]' , aux )
} else { // 'NO' == lstParticipants[0] && 'NO' != corporate
def participantWithCorp = ''
participantWithCorp = participants_participantWithCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML ) party_participants_XML = party_participants_XML.replace( '[PARTICIPANTwithCORP]' , participantWithCorp ) .replace( '[PARTICIPANT]' , '' )
}
// CONSTRUCCION FINAL DEL PARTY if ( 'NO' == corporate.toUpperCase() ) {
party_senderWithoutCorp_XML = party_senderWithoutCorp_XML.replace( '[IATAorNOIATA]' , party_IATA_XML ) party_XML = party_XML.replace( '[SENDER]' , party_senderWithoutCorp_XML ) .replace( '[PARTICIPANTS]' , party_participants_XML ) } else { // 'NO' != corporate party_senderWithCorp_XML = party_senderWithCorp_XML.replace( '[CORPORATE]' , corporate ) party_XML = party_XML.replace( '[SENDER]' , party_senderWithCorp_XML ) .replace( '[PARTICIPANTS]' , party_participants_XML ) }
return party_XML } }
final objName = "cntx_GXML" context.setProperty( objName, new LIB_GeneralXMLs ( testRunner, context, log) )

/*
 *	XML:
 *		- getQuery_XML
 *		- getQualifier_XML
 */

import groovy.json.JsonSlurper

class LIB_OfferPriceXMLs {

	//XMLs
	def query_XML		= """<Query>
							<Offer OfferID="[OFFERID]" Owner="IB" ResponseID="[RESPONSEID]">
								[OFFERITEMS]
							</Offer>
						</Query>"""

	def offerItem_XML	= """<OfferItem OfferItemID="[OFFERITEM]">
							<PassengerRefs>[TAGS]</PassengerRefs>
						</OfferItem>"""

	def qualifier_XML	= """<Qualifier>
				            <SpecialNeedQualifiers>
				               <Code>DISA</Code>
				            </SpecialNeedQualifiers>
				         </Qualifier>"""


	def public testRunner, context, logger

	def LIB_OfferPriceXMLs ( whichTestRunner, whichContext, whichLogger ){
		this.logger	= whichLogger
		this.context	= whichContext
		this.testRunner = whichTestRunner
	}

		//Devuelve XML de Query
	def getQuery_XML( 	numADT, numCHD, numINF, responseID,
					offerItemID_ADT, offerItemID_CHD, offerItemID_INF, offerID ) {
		logger.info ':: RUNNING -> //Libs/LIB_OfferPrice XMLs/getQuery_XML'

		def tags 		= ''
		def offerItems	= ''

		for ( int i=0; i < numADT.toInteger(); i++ ){
			tags	= tags + "ADULT_0" + (i+1)
			if ( i != (numADT.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_ADT != ""  && offerItemID_ADT != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_ADT).replace("[TAGS]", tags)
		}

		tags = ""
		for ( int i=0; i < numCHD.toInteger(); i++ ){
			tags	= tags + "CHILD_0" + (i+1)
			if ( i != (numCHD.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_CHD != "" && offerItemID_CHD != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_CHD).replace("[TAGS]", tags)
		}

		tags = ""
		for ( int i=0; i < numINF.toInteger(); i++ ){
			tags	= tags + "INFANT_0" + (i+1)
			if ( i != (numINF.toInteger()-1)) {
				tags	= tags + " "
			}
		}

		if ( offerItemID_INF != "" && offerItemID_INF != 'N/A' ) {
			offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", offerItemID_INF).replace("[TAGS]", tags)
		}

		query_XML	= query_XML.replace("[OFFERID]"	, offerID		)
						 .replace("[RESPONSEID]"	, responseID	)
						 .replace("[OFFERITEMS]"	, offerItems	)		

		return query_XML
	}	

		//Devuelve XML de CoreQuery
	def getQualifier_XML( specialNeeds ) {
		logger.info ':: RUNNING -> //Libs/LIB_OfferPrice XMLs/getQualifier_XML'

		if( '' == specialNeeds || 'NO' == specialNeeds ) {
			qualifier_XML	= ''
		} // else manda el XML tal cual

		return qualifier_XML
	}
}

final objName	= "cntx_OPXML"
context.setProperty( objName, new LIB_OfferPriceXMLs ( testRunner, context, log) )

/*
 *	XML:
 *		- getBagsOffer_XML
 *		- getSeatsOffer_XML
 *		- getValidSeats
 *		- getSeat
 *		- getSeatsOfferItem_XML
 *		- getSeatOffer_XML
 *		- getSeatsAmount
 */

import groovy.json.JsonSlurper

class LIB_AncillariesXMLs {

	//XMLs							
def offerAnc_XML	= """	<Offer OfferID="[OFFER]" Owner="IB" ResponseID="[RESPONSEID]">
							[OFFERSANC]
						</Offer>"""

def offerItem_XML	= """	<OfferItem OfferItemID="[OFFERITEM]">
                     			<PassengerRefs>[TAG]</PassengerRefs>
                  			</OfferItem>"""

def seat_XML		= """	<OfferItem OfferItemID="[OFFERITEM]">
							<PassengerRefs>[TAG]</PassengerRefs>
							<ALaCarteSelection>
								<Quantity>1</Quantity>
								<SegmentID>[SEGMENT]</SegmentID>
							</ALaCarteSelection>
							<SeatSelection>
								<Row>[ROW]</Row>
								<Column>[COLUMN]</Column>
							</SeatSelection>
						</OfferItem>"""

	def public testRunner, context, logger

	def LIB_AncillariesXMLs ( whichTestRunner, whichContext, whichLogger ){
		this.logger	= whichLogger
		this.context	= whichContext
		this.testRunner = whichTestRunner
	}

		//Devuelve XML del offer de maletas
	def getBagsOffer_XML( rs, nADT, nCHD, bags, segmentLstV2 ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getBagsOffer_XML'

		def bagsOffer_XML	= ''
		def offersBaggage_xml	= ''
		def bagsAmount		= 0.00
		def bagPrice		= 0.00
		def item			= ''
		def numRoutes		= segmentLstV2.toString().split('],').size()
		bags				= bags.split(',')

		segmentLstV2		= segmentLstV2	.replace( '[' , '' )
									.replace( ']]', '' )
		segmentLstV2		= segmentLstV2.split('], ')

		assert rs.Body.ServiceListRS.ALaCarteOffer.size() > 0 : 'No hay disponible servicios de maleta'

		def orderID	= rs.Body.ServiceListRS.ALaCarteOffer.@OfferID.toString()
		def responseID	= rs.Body.ServiceListRS.ShoppingResponseID.ResponseID.toString()

		def offers		= rs.Body.ServiceListRS.ALaCarteOffer
		def offerIDBaggage	= offers.@OfferID.toString()
		def idx			= 0

		bags.each {				
			if ( it != "NO") {

				assert bags.size() == numRoutes, 'ERROR: El numero de rutas no coincide con el número de valores dado en la propiedad \'anc_bags\'.' 

				def optSelected = it.toString()

				if ( optSelected == "firstsvc" )	{	optSelected = "1" }
				else 						{	optSelected = it.toString() }

				def auxSegment	= segmentLstV2[idx].substring(0,14)

				for ( int i = 0; i < nADT.toInteger(); i++ ){
					def adult	= "ADULT_0" + (i+1)
					def offerItem	= ""
					def offerADT	= ""
					offers.ALaCarteOfferItem.each { offer ->
						if (	offer.Eligibility.PassengerRefs.toString().contains(adult.toString()) && 
							offer.Eligibility.SegmentRefs.toString().contains(auxSegment.toString()) &&
							offer.Service.ServiceDefinitionRef.toString().contains(optSelected.toString()) ) {
								offerADT 		= offer.@OfferItemID.toString()
								bagPrice		= Double.parseDouble(offer.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toString())
								bagsAmount 	= bagsAmount + bagPrice
						}
					}	

					assert offerADT != '', "ERROR: Pasajero: $adult / Segmento: [$idx]$auxSegment -> No se ha encontrado servicio de maleta"

					offersBaggage_xml = offersBaggage_xml + offerItem_XML	.replace("[OFFERITEM]", offerADT)
																.replace("[TAG]", adult)
				}

				for ( int i = 0; i < nCHD.toInteger(); i++ ){
					def child	= "CHILD_0" + (i+1)
					def offerItem	= ""
					def offerCHD	= ""

					offers.ALaCarteOfferItem.each(){ offer ->
						if (	offer.Eligibility.PassengerRefs.toString().contains(child.toString()) && 
							offer.Eligibility.SegmentRefs.toString().contains(auxSegment.toString()) &&
							offer.Service.ServiceDefinitionRef.toString().contains(optSelected.toString()) ) {
								offerCHD = offer.@OfferItemID.toString()							
								bagPrice		= Double.parseDouble(offer.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toString())
								bagsAmount 	= bagsAmount + bagPrice
						}
					}

					assert offerCHD != '', "ERROR: Pasajero: $child / Segmento: [$idx]$auxSegment -> No se ha encontrado servicio de maleta"

					offersBaggage_xml = offersBaggage_xml + offerItem_XML	.replace("[OFFERITEM]", offerCHD)
																.replace("[TAG]", child)
				}																		     
			}
			idx++
		}

		bagsOffer_XML = offerAnc_XML	.replace("[OFFER]"		, orderID			)
								.replace("[RESPONSEID]"	, responseID		)
								.replace("[OFFERSANC]"	, offersBaggage_xml	)		

		return [ bagsOffer_XML, bagsAmount.toString() ]
	}

		//Devuelve XML del offer de asientos
	def getSeatsOffer_XML( rs, nADT, nCHD, seats, seat_code, 
						segmentLst, segmentLstV2 ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML'

		assert rs.Body.SeatAvailabilityRS.ALaCarteOffer.size() > 0, "ERROR: NO hay servicios de asientos disponibles"

		def seatsOffer_XML 		= ''
		def seatsOfferItem_XML 	= ''
		def seatsAmount		= 0.00
		def auxPrice			= 0.00
		def lstPAX			= []
		def segmentsWithSeat	= []
		def validSeats			= []
		def offerItemID		= ''
		def seatsSelected		= []
		def seatSelected		= ''
		def bolSeatSelected		= false

		def offerID	= rs.Body.SeatAvailabilityRS.ALaCarteOffer.@OfferID.toString()
		def responseID	= rs.Body.SeatAvailabilityRS.ShoppingResponseID.ResponseID.toString()

		(1..nADT.toInteger()).each() { 
			lstPAX << 'ADULT_0' + it
		}

		(1..nCHD.toInteger()).each() { 
			lstPAX << 'CHILD_0' + it
		}

			//Para saber el número de rutas con solo segmentLstV2
		segmentLstV2		= segmentLstV2	.replace( '[' , '' )
									.replace( ']]', '' )
		segmentLstV2		= segmentLstV2.split('], ')
		def numRoutes		= segmentLstV2.size()
			/////////////////////////////////////////////////////

		seats	= seats.split(',')

			//Se crea lista con los segmentos que llevarán asientos
		if ( seats.size() == numRoutes ) { 
			segmentLstV2.each() {
				segmentsWithSeat	<< it.substring(0,14)
			}			
		} else {
			segmentsWithSeat	= segmentLst.split(', ')
		}
			///////////////////////////////////////////////////////

		segmentsWithSeat.eachWithIndex() { segm, idx ->
			def optionSeleced	= seats[idx].toString().toUpperCase()
			seatsSelected		= []
			if( optionSeleced == 'PAY' || optionSeleced == 'PAGO' || optionSeleced == 'P' ) {
				lstPAX.each() { pax ->
					bolSeatSelected	= false
					validSeats		= []
					rs.Body.SeatAvailabilityRS.ALaCarteOffer.ALaCarteOfferItem.each(){
						if (	it.Eligibility.PassengerRefs.toString().contains(pax) 	&&
							it.Eligibility.SegmentRefs.toString().equals(segm)	&&
							it.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toDouble() > 0.00 &&
							bolSeatSelected	== false ) {
								validSeats					= getValidSeats( rs, segm, seat_code, it.@OfferItemID.toString(), seatsSelected )
								( seatSelected, bolSeatSelected )	= getSeat( validSeats )
								offerItemID	= it.@OfferItemID.toString()
						}
					}
					assert seatSelected != '', "ERROR: Pasajero: $pax / Segmento: [$idx]$segm / Code: $seat_code  -> No se han encontrado asientos de pago disponibles"
					seatsOfferItem_XML	= getSeatsOfferItem_XML( seatsOfferItem_XML, segm, pax, seatSelected, offerItemID )
					seatsAmount	= getSeatsAmount ( rs, seatsAmount, offerItemID )
				}				
			} else if ( optionSeleced == 'FREE' || optionSeleced == 'GRATIS' || optionSeleced == 'F' || optionSeleced == 'G' ) {
				lstPAX.each() { pax ->
					bolSeatSelected	= false
					validSeats		= []
					rs.Body.SeatAvailabilityRS.ALaCarteOffer.ALaCarteOfferItem.each(){
						if (	it.Eligibility.PassengerRefs.toString().contains(pax) 	&&
							it.Eligibility.SegmentRefs.toString().equals(segm)	&&
							it.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toDouble() == 0.00 &&
							bolSeatSelected	== false ) {
								validSeats					= getValidSeats( rs, segm, seat_code, it.@OfferItemID.toString(), seatsSelected )
								( seatSelected, bolSeatSelected )	= getSeat( validSeats )
								offerItemID	= it.@OfferItemID.toString()
						}
					}
					assert seatSelected != '', "ERROR: Pasajero: $pax / Segmento: [$idx]$segm / Code: $seat_code  -> No se han encontrado asientos gratis disponibles"
					seatsOfferItem_XML	= getSeatsOfferItem_XML( seatsOfferItem_XML, segm, pax, seatSelected, offerItemID )
					seatsAmount	= getSeatsAmount ( rs, seatsAmount, offerItemID )
				}
			} else if ( optionSeleced == 'ANY' || optionSeleced == 'CUALQUIERA' || optionSeleced == 'A' || optionSeleced == 'C' ) {
				lstPAX.each() { pax ->
					bolSeatSelected	= false
					validSeats		= []
					rs.Body.SeatAvailabilityRS.ALaCarteOffer.ALaCarteOfferItem.each(){
						if (	it.Eligibility.PassengerRefs.toString().contains(pax) 	&&
							it.Eligibility.SegmentRefs.toString().equals(segm)	&&
							bolSeatSelected	== false ) {
								validSeats					= getValidSeats( rs, segm, seat_code, it.@OfferItemID.toString(), seatsSelected )
								( seatSelected, bolSeatSelected )	= getSeat( validSeats )
								offerItemID	= it.@OfferItemID.toString()
						}
					}
					assert seatSelected != '', "ERROR: Pasajero: $pax / Segmento: [$idx]$segm / Code: $seat_code  -> No se han encontrado asientos (ni gratis/ni de pago) disponibles"
					seatsOfferItem_XML	= getSeatsOfferItem_XML( seatsOfferItem_XML, segm, pax, seatSelected, offerItemID )
					seatsAmount	= getSeatsAmount ( rs, seatsAmount, offerItemID )
				}
			} else {
				logger.warn "Para el segmento [$idx]$segm no se han pedido servicio de asientos"
			}
		}

		seatsOffer_XML	= getSeatOffer_XML( offerID, responseID, seatsOfferItem_XML )

		return [ seatsOffer_XML, seatsAmount.toString() ]
	}

		//Se obtiene los asientos válidos para el pasajero para el OfferItem pasado por párametro
	def getValidSeats ( rs, segm, seat_code, offerItemID, seatsSelected ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML/getValidSeats'

		def validSeats	= []
		def validRows	= []
		def seatCodes	= []

		def seatMap	= rs.Body.SeatAvailabilityRS.SeatMap.find() {
			it.SegmentRef.toString().equals(segm)
		}

		seatMap.Cabin.Row.each() { row ->
			row.Seat.each() { seat ->
				seatCodes	= []
				seat.SeatCharacteristics.Code.each(){
					seatCodes	<< it.toString()
				}

				if ( 'NO' == seat_code ) { 
					if ( seat.OfferItemRefs.toString().contains(offerItemID)  ) {
						validSeats << ( row.Number.toString() + seat.Column.toString() )
					}
				} else {
					if ( seat.OfferItemRefs.toString().contains(offerItemID) && seatCodes.contains(seat_code)  ) {
						validSeats << ( row.Number.toString() + seat.Column.toString() )
					}
				}
			}					
		}

		validSeats.removeAll(seatsSelected)

		return validSeats 
	}

		//Se obtiene un asiento aleatorio de los válidos obtenidos anteriormente
	def getSeat( validSeats ){
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML/getSeat'

		def rnd	= new Random()
		def seatSelected
		def bolSeatSelected

		if ( '' != validSeats && null != validSeats ) {
			seatSelected		= validSeats[ rnd.nextInt( validSeats.size() ) ]
			bolSeatSelected	= true
		} else {
			bolSeatSelected	= false
		}

		return [ seatSelected, bolSeatSelected ]
	}

		//Se crea XML de los offerItems para asientos
	def getSeatsOfferItem_XML( seatsOfferItem_XML, segm, pax, seatSelected, offerItemID ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML/getSeatsOfferItem_XML'

		def numRow	= ''
		def column	= ''
		def iCulumn	= ( seatSelected.size()-1 )
		column	= seatSelected.substring(iCulumn)
		numRow	= seatSelected.substring(0, iCulumn)

		seatsOfferItem_XML	= seatsOfferItem_XML + seat_XML	.replace( '[OFFERITEM]'	, offerItemID 	)
													.replace( '[TAG]'		, pax 		)
													.replace( '[SEGMENT]'	, segm		)
													.replace( '[ROW]', 		, numRow		)
													.replace( '[COLUMN]'	, column 		)

		return seatsOfferItem_XML
	}

		//Se crea XML del Offer con todos los offerItems de los asientos
	def getSeatOffer_XML ( offerID, responseID, seatsOfferItem_XML ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML/getSeatOffer_XML'

		def seatOffer_XML	= offerAnc_XML	.replace( '[OFFER]'		, offerID			)
									.replace( '[RESPONSEID]'	, responseID		)
									.replace( '[OFFERSANC]'	, seatsOfferItem_XML)

		return seatOffer_XML
	}

		//Se obtiene el precio del asiento y se añade al total de asientos
	def getSeatsAmount( rs, seatsAmount, offerItemID ) {
		logger.info ':: RUNNING -> //Libs/LIB_Ancillaries XMLs/getSeatsOffer_XML/getSeatsAmount'

		def seatPrice	= rs.Body.SeatAvailabilityRS.ALaCarteOffer.ALaCarteOfferItem.find() {
			it.@OfferItemID.toString().equals(offerItemID)
		}.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toString()

		seatsAmount	= seatsAmount + Double.parseDouble(seatPrice)		

		return seatsAmount
	}
}

final objName	= "cntx_AXML"
context.setProperty( objName, new LIB_AncillariesXMLs ( testRunner, context, log) )

/* * XML: * - getOrder_XML * - getServiceFee_XML * - getPayments_XML * - getDataList_XML * - getIdentityDocument_XML * - getRemark_XML * - getQueryOrderCreate_XML * - getOfferSpecialNeeds_XML */
import groovy.json.JsonSlurper
class LIB_OrderCreateXMLs {
//XMLs def query_XML = """<Query> [ORDER] [PAYMENTS] [DATALISTS] </Query>"""
def order_XML = """<Order> <Offer OfferID="[OFFERID]" Owner="IB" ResponseID="[RESPONSEID]"> [OFFERITEMS] </Offer> [SPECIALNEEDS] [BAGS] [SEATS] </Order>"""
def specialNeeds_XML = """<Offer OfferID="[OFFERID]" Owner="IB" ResponseID="[RESPONSEID]"> [OFFERITEMS] </Offer>"""
def offerItemSN_XML = """ <OfferItem OfferItemID="[OFFERITEM]"> <PassengerRefs>[PAX]</PassengerRefs> <ServiceSelection ServiceID="[SERVICEID]"> <ServiceDefinitionID>[DEFINITION]</ServiceDefinitionID> </ServiceSelection> </OfferItem>"""
def serviceDefSN_XML = """<ServiceDefinitionList> [SERVICESDEF] </ServiceDefinitionList>"""
def serviceSN_XML = """<ServiceDefinition ServiceDefinitionID="[DEF]"> <Name>[NAME]</Name> <Descriptions> <Description refs="[PAX]"> <Text>[TEXT]</Text> </Description> </Descriptions> <BookingInstructions> <SSRCode>[CODE]</SSRCode> </BookingInstructions> </ServiceDefinition>"""
def offerItem_XML = """<OfferItem OfferItemID="[OFFERITEM]"> <PassengerRefs>[TAGS]</PassengerRefs> </OfferItem>"""
def serviceFee_XML = """<Payment> <Method> <Other> <Remarks> <Remark>ServiceFee</Remark> </Remarks> </Other> </Method> <Amount Code="EUR">[AMOUNT]</Amount> <Associations> <Passengers> <PassengerReference>[TAGS]</PassengerReference> </Passengers> </Associations> </Payment>"""
def dataList_XML = """ <DataLists> <PassengerList> [PASSENGERLIST] </PassengerList> [CONTACTLIST] [SERVICEDEF] </DataLists>"""
def passenger_XML = """ <Passenger PassengerID="[PAX]"> <PTC>[PTC]</PTC> <Birthdate>[BIRTHDATE]</Birthdate> <Individual IndividualID="[INDPAX]"> <Gender>[GENDER]</Gender> <NameTitle>[NAMETITLE]</NameTitle> <GivenName>[GIVENNAME]</GivenName> <Surname>[SURNAME]</Surname> </Individual> <FOID> <FOID_Number>[FOIDNUMBER]</FOID_Number> <FOID_Type>[FOIDTYPE]</FOID_Type> </FOID> [IDENTITYDOCUMENT] <ContactInfoRef>[CONTACTINFOREF]</ContactInfoRef> [REMARK] </Passenger>"""
def identityDocument_XML = """ <IdentityDocument> <IdentityDocumentNumber>[DOCNUM]</IdentityDocumentNumber> <IdentityDocumentType>[TYPE]</IdentityDocumentType> </IdentityDocument>"""
def remark_XML = """ <Remark> <Remark>[REMARKCODE]</Remark> </Remark>"""
def contactList_XML = """ <ContactList> <ContactInformation ContactID="ADULT_01_CONTACT"> <ContactType/> <ContactProvided> <EmailAddress> <EmailAddressValue>[EMAIL]</EmailAddressValue> </EmailAddress> </ContactProvided> <ContactProvided> <Phone> <Label>Passenger</Label> <PhoneNumber>+34666554433</PhoneNumber> </Phone> <Phone> <Label>Emergency</Label> <PhoneNumber>34666554433</PhoneNumber> </Phone> </ContactProvided> <Individual> <GivenName>CONTACTO</GivenName> <Surname>EMERGENCIA</Surname> </Individual> </ContactInformation> <ContactInformation ContactID="Payer"> <ContactType>Payer</ContactType> <ContactProvided> <EmailAddress> <EmailAddressValue>[EMAILPAYER]</EmailAddressValue> </EmailAddress> </ContactProvided> <IndividualRef>IND_ADULT_01</IndividualRef> </ContactInformation> </ContactList>"""
def public testRunner, context, logger
def LIB_OrderCreateXMLs ( whichTestRunner, whichContext, whichLogger ){ this.logger = whichLogger this.context = whichContext this.testRunner = whichTestRunner }
//Devuelve XML de Order def getOrder_XML( oi_ADT, oi_CHD, oi_INF, nADT, nCHD, nINF, offerID, seatsOffer_XML, bagsOffer_XML, offerSpecialNeeds_XML, rsID ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getOrder_XML'
def tags = '' def offerItems = ''
for ( int i=0; i < nADT.toInteger(); i++ ){ tags = tags + "ADULT_0" + (i+1) if ( i != (nADT.toInteger()-1)) { tags = tags + " " } }
if ( oi_ADT != "" && oi_ADT != 'N/A' ) { offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", oi_ADT).replace("[TAGS]", tags) }
tags = "" for ( int i=0; i < nCHD.toInteger(); i++ ){ tags = tags + "CHILD_0" + (i+1) if ( i != (nCHD.toInteger()-1)) { tags = tags + " " } }
if ( oi_CHD != "" && oi_CHD != 'N/A' ) { offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", oi_CHD).replace("[TAGS]", tags) }
tags = "" for ( int i=0; i < nINF.toInteger(); i++ ){ tags = tags + "INFANT_0" + (i+1) if ( i != (nINF.toInteger()-1)) { tags = tags + " " } }
if ( oi_INF != "" && oi_INF != 'N/A' ) { offerItems = offerItems + offerItem_XML.replace("[OFFERITEM]", oi_INF).replace("[TAGS]", tags) }
if ( 'to be automatically filled' == bagsOffer_XML ) { bagsOffer_XML = '' } if ( 'to be automatically filled' == seatsOffer_XML ) { seatsOffer_XML = '' }
order_XML = order_XML.replace("[OFFERID]" , offerID ) .replace("[RESPONSEID]" , rsID ) .replace("[OFFERITEMS]" , offerItems ) .replace("[SPECIALNEEDS]" , offerSpecialNeeds_XML ) .replace("[BAGS]" , bagsOffer_XML ) .replace("[SEATS]" , seatsOffer_XML )
return order_XML }
//Devuelve XML del ServiceFee def getServiceFee_XML( nADT, nCHD, nINF, serviceFee ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getServiceFee_XML'
def sf_XML = ''
if ( serviceFee == 'NO' || serviceFee == '' ) { sf_XML = '' } else { serviceFee = serviceFee.split(",")
if ( serviceFee[0] != '' ) { def tagADT = '' def idx = 1 ( 1..nADT.toInteger() ).each() { tagADT = tagADT + "ADULT_0" + idx if (idx != nADT.toInteger()) tagADT = tagADT + " " idx++ } sf_XML = sf_XML + serviceFee_XML.replace("[AMOUNT]", serviceFee[0].toString()) .replace("[TAGS]", tagADT) }
if ( serviceFee[1] != '' ) { def tagCHD = '' def idx = 1 ( 1..nCHD.toInteger() ).each() { tagCHD = tagCHD + "CHILD_0" + idx if (idx != nCHD.toInteger()) tagCHD = tagCHD + " " idx++ } sf_XML = sf_XML + serviceFee_XML.replace("[AMOUNT]", serviceFee[1].toString()) .replace("[TAGS]", tagCHD) }
if ( serviceFee[2] != '' ) { def tagINF = '' def idx = 1 ( 1..nINF.toInteger() ).each() { tagINF = tagINF + "INFANT_0" + idx if (idx != nINF.toInteger()) tagINF = tagINF + " " idx++ } sf_XML = sf_XML + serviceFee_XML.replace("[AMOUNT]", serviceFee[2].toString()) .replace("[TAGS]", tagINF) } }
return sf_XML }
//Eliminar decimales del importe y dejarlo solo con nada más que dos. def round(numberStr){ return Math.round(Double.parseDouble(numberStr) * 100) / 100 }
//Devuelve XML del Payments def getPayments_XML( mashery, version, paymentMethod_order, currency, totalPriceToPay, bagsAmount, seatsAmount, emailPayer, cardNum, cardType, TestSteps, maskedCreditCard, tokenizedCreditCard, serviceFee_XML, specialNeedsAmount ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getPayments_XML'
def payments_XML = ''
if ( 'CASH' == paymentMethod_order ) { payments_XML = TestSteps.getPropertyValue("CASH_ORDERCREATE_v$version") } else { payments_XML = TestSteps.getPropertyValue("${mashery}_MASHERY_CC_ORDERCREATE_v$version") }
if ( 'to be automatically filled' == bagsAmount ) { bagsAmount = '0.00' } if ( 'to be automatically filled' == seatsAmount ) { seatsAmount= '0.00' }
def total = Double.parseDouble( totalPriceToPay ) + Double.parseDouble( bagsAmount ) + Double.parseDouble( seatsAmount ) + Double.parseDouble( specialNeedsAmount ) totalPriceToPay = round(total.toString()).toString()
payments_XML = payments_XML .replace( '[CURRENCY]' , currency ) .replace( '[AMOUNT]' , totalPriceToPay ) .replace( '[CARDTYPE]' , cardType ) .replace( '[CARDNUM]' , cardNum ) .replace( '[EMAILPAYER]' , emailPayer ) .replace( '[SERVICEFEE]' , serviceFee_XML ) .replace( '[MASKEDCREDITCARD]' , maskedCreditCard ) .replace( '[TOKENIZEDCREDITCARD]' , tokenizedCreditCard )
return [ payments_XML, totalPriceToPay ] }
//Devuelve XML del DataList def getDataList_XML( nADT, nCHD, nINF, lst_adults, lst_childs, lst_infants, residentDisc, largeFamilyDisc, email, emailPayer, serviceDefSN_XML ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getDataList_XML'
def json = new JsonSlurper() def json_lst_adults = json.parseText(lst_adults) def json_lst_childs = json.parseText(lst_childs) def json_lst_infants = json.parseText(lst_infants)
def passengerLst_XML = '' def string_PAX = '' def string_indPAX = ''
//ADULT if ( 0 < nADT.toInteger() ) { (0..(nADT.toInteger()-1)).each(){ string_PAX = 'ADULT_0' + (it+1).toString() string_indPAX = 'IND_ADULT_0' + (it+1).toString() passengerLst_XML = passengerLst_XML + passenger_XML .replace( '[PAX]' , string_PAX ) .replace( '[PTC]' , 'ADT' ) .replace( '[BIRTHDATE]' , json_lst_adults[it].Birthdate ) .replace( '[INDPAX]' , string_indPAX ) .replace( '[GENDER]' , json_lst_adults[it].Gender ) .replace( '[NAMETITLE]' , json_lst_adults[it].NameTitle ) .replace( '[GIVENNAME]' , json_lst_adults[it].GivenName ) .replace( '[SURNAME]' , json_lst_adults[it].Surname ) .replace( '[FOIDNUMBER]' , json_lst_adults[it].FOID_Number ) .replace( '[FOIDTYPE]' , json_lst_adults[it].FOID_Type ) .replace( '[CONTACTINFOREF]' , json_lst_adults[it].ContactInfoRef )
if ( ('NO' != residentDisc && '' != residentDisc) || ('NO' != largeFamilyDisc && '' != largeFamilyDisc) ) { passengerLst_XML = getIdentityDocument_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) passengerLst_XML = getRemark_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) } else { passengerLst_XML = passengerLst_XML.replace( '[IDENTITYDOCUMENT]' , '' ) passengerLst_XML = passengerLst_XML.replace( '[REMARK]' , '' ) } } }
//CHILD if ( 0 < nCHD.toInteger() ) { (0..(nCHD.toInteger()-1)).each(){ string_PAX = 'CHILD_0' + (it+1).toString() string_indPAX = 'IND_CHILD_0' + (it+1).toString() passengerLst_XML = passengerLst_XML + passenger_XML .replace( '[PAX]' , string_PAX ) .replace( '[PTC]' , 'CHD' ) .replace( '[BIRTHDATE]' , json_lst_childs[it].Birthdate ) .replace( '[INDPAX]' , string_indPAX ) .replace( '[GENDER]' , json_lst_childs[it].Gender ) .replace( '[NAMETITLE]' , json_lst_childs[it].NameTitle ) .replace( '[GIVENNAME]' , json_lst_childs[it].GivenName ) .replace( '[SURNAME]' , json_lst_childs[it].Surname ) .replace( '[FOIDNUMBER]' , json_lst_childs[it].FOID_Number ) .replace( '[FOIDTYPE]' , json_lst_childs[it].FOID_Type ) .replace( '[CONTACTINFOREF]' , json_lst_childs[it].ContactInfoRef )
if ( ('NO' != residentDisc && '' != residentDisc) || ('NO' != largeFamilyDisc && '' != largeFamilyDisc) ) { passengerLst_XML = getIdentityDocument_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) passengerLst_XML = getRemark_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) } else { passengerLst_XML = passengerLst_XML.replace( '[IDENTITYDOCUMENT]' , '' ) passengerLst_XML = passengerLst_XML.replace( '[REMARK]' , '' ) } } }
//INFANT if ( 0 < nINF.toInteger() ) { (0..(nINF.toInteger()-1)).each(){ string_PAX = 'INFANT_0' + (it+1).toString() string_indPAX = 'IND_INFANT_0' + (it+1).toString() passengerLst_XML = passengerLst_XML + passenger_XML .replace( '[PAX]' , string_PAX ) .replace( '[PTC]' , 'INF' ) .replace( '[BIRTHDATE]' , json_lst_infants[it].Birthdate ) .replace( '[INDPAX]' , string_indPAX ) .replace( '[GENDER]' , json_lst_infants[it].Gender ) .replace( '[NAMETITLE]' , json_lst_infants[it].NameTitle ) .replace( '[GIVENNAME]' , json_lst_infants[it].GivenName ) .replace( '[SURNAME]' , json_lst_infants[it].Surname ) .replace( '[FOIDNUMBER]' , json_lst_infants[it].FOID_Number ) .replace( '[FOIDTYPE]' , json_lst_infants[it].FOID_Type ) .replace( '[CONTACTINFOREF]' , json_lst_infants[it].ContactInfoRef )
if ( ('NO' != residentDisc && '' != residentDisc) || ('NO' != largeFamilyDisc && '' != largeFamilyDisc) ) { passengerLst_XML = getIdentityDocument_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) passengerLst_XML = getRemark_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) } else { passengerLst_XML = passengerLst_XML.replace( '[IDENTITYDOCUMENT]' , '' ) passengerLst_XML = passengerLst_XML.replace( '[REMARK]' , '' ) } } }
contactList_XML = contactList_XML .replace( '[EMAIL]' , email ) .replace( '[EMAILPAYER]' , emailPayer )
dataList_XML = dataList_XML .replace( '[PASSENGERLIST]' , passengerLst_XML ) .replace( '[CONTACTLIST]' , contactList_XML ) .replace( '[SERVICEDEF]' , serviceDefSN_XML ) }
//Devuelve XML de identityDocument para el passenger_XML def getIdentityDocument_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getIdentityDocument_XML'
def identityDoc_residents = "12313123111" def identityDoc_largFamily = "123131414143" def identityDocument = ''
if ( 'NO' != residentDisc && '' != residentDisc ) { identityDocument = identityDocument_XML .replace( '[DOCNUM]' , identityDoc_residents ) .replace( '[TYPE]' , 'F1' ) } else { identityDocument = '' }
if ( 'NO' != largeFamilyDisc && '' != largeFamilyDisc ) { identityDocument = identityDocument + identityDocument_XML .replace( '[DOCNUM]' , identityDoc_largFamily ) .replace( '[TYPE]' , 'AI' ) }
passengerLst_XML = passengerLst_XML.replace( '[IDENTITYDOCUMENT]' , identityDocument )
return passengerLst_XML }
//Devuelve XML de remark para el passenger_XML def getRemark_XML( residentDisc, largeFamilyDisc, passengerLst_XML ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getRemark_XML'
def RC_code = 'RESIDENT-380201' def BP_code = 'RESIDENT-070099' def RM_code = 'RESIDENT-520018' def LF_code = 'LARGEFAMILY-060830' def remark = ''
if ( 'NO' != residentDisc && '' != residentDisc ) { switch (residentDisc) { case 'RC': remark = remark_XML .replace( '[REMARKCODE]' , RC_code ) break case 'BP': remark = remark_XML .replace( '[REMARKCODE]' , BP_code ) break case 'RM': remark = remark_XML .replace( '[REMARKCODE]' , RM_code ) break default: context.getTestRunner().fail(" ERROR: descuento de residentes no válido. Comprobar el valor del parámetro 'residentDiscount'" ) break } } else { remark = '' }
if ( 'NO' != largeFamilyDisc && '' != largeFamilyDisc ) { if ( 'F1' == largeFamilyDisc || 'F2' == largeFamilyDisc ) { remark = remark + remark_XML .replace( '[REMARKCODE]' , LF_code ) } else { context.getTestRunner().fail(" ERROR: descuento de familia numerosa no válido. Comprobar el valor del parámetro 'largeFamilyDiscount'" ) } }
passengerLst_XML = passengerLst_XML.replace( '[REMARK]' , remark )
return passengerLst_XML }
//Devuelve XML de la query def getQueryOrderCreate_XML( order_XML, payments_XML, dataLists_XML ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getQueryOrderCreate_XML'
query_XML = query_XML .replace( '[ORDER]' , order_XML ) .replace( '[PAYMENTS]' , payments_XML ) .replace( '[DATALISTS]' , dataLists_XML )
return query_XML }
//Devuelve XML del Offer para SpecialNeeds def getOfferSpecialNeeds_XML( rs, specialNeeds ) { logger.info ':: RUNNING -> //Libs/LIB_OrderCreate XMLs/getOfferSpecialNeeds_XML'
def XML_offerSN = '' def XML_serviceSN = '' def pax = [] def needs = '' def offerItemSN = '' def serviceSN = '' def offerItem = '' def serviceID = '' def definition = '' def name = '' def text = '' def code = '' def specialNeedsAmount = 0.00 List auxSN
def responseID = rs.ShoppingResponseID.ResponseID.toString() def offerID = rs.OtherOffers.ALaCarteOffer.@OfferID.toString()
specialNeeds = specialNeeds.split(',')
specialNeeds.each() { auxSN = it.toString().split(' ') pax = auxSN[0] auxSN.removeAt(0)
auxSN.each() { need ->
offerItemSN = rs.OtherOffers.ALaCarteOffer.ALaCarteOfferItem.find() { it.Eligibility.PassengerRefs.toString().contains(pax.toString()) && it.@OfferItemID.toString().contains(need.toString()) }
assert offerItemSN != '', "ERROR: No se ha encontrado oferta con necesidad especial $need"
specialNeedsAmount = specialNeedsAmount + Double.parseDouble(offerItemSN.UnitPriceDetail.TotalAmount.SimpleCurrencyPrice.toString())
offerItem = offerItemSN.@OfferItemID.toString() serviceID = offerItemSN.Service.@ServiceID.toString() definition = offerItemSN.Service.ServiceDefinitionRef.toString()
XML_offerSN = XML_offerSN + offerItemSN_XML .replace( '[OFFERITEM]' , offerItem ) .replace( '[PAX]' , pax ) .replace( '[SERVICEID]' , serviceID ) .replace( '[DEFINITION]' , definition )
serviceSN = rs.DataLists.ServiceDefinitionList.ServiceDefinition.find() { it.@ServiceDefinitionID.toString().equals(definition) }
assert serviceSN != '', "ERROR: No se ha encontrado servicio con defincion '$definition'"
name = serviceSN.Name.toString() text = serviceSN.Descriptions.Description.Text.toString() code = serviceSN.BookingInstructions.SSRCode.toString()
XML_serviceSN = XML_serviceSN + serviceSN_XML .replace( '[DEF]' , definition ) .replace( '[NAME]' , name ) .replace( '[PAX]' , pax ) .replace( '[TEXT]' , text ) .replace( '[CODE]' , code ) }
XML_offerSN = specialNeeds_XML .replace( '[OFFERID]' , offerID ) .replace( '[RESPONSEID]' , responseID ) .replace( '[OFFERITEMS]' , XML_offerSN )
XML_serviceSN = serviceDefSN_XML .replace( '[SERVICESDEF]' , XML_serviceSN ) }
return [ specialNeedsAmount.toString(), XML_offerSN, XML_serviceSN ] } }
final objName = "cntx_OCXML" context.setProperty( objName, new LIB_OrderCreateXMLs ( testRunner, context, log) )

/* * XML: * - getReshop_XML */
import groovy.json.JsonSlurper
class LIB_OrderReshopXMLs {
orderItem_XML = """<OrderItem OrderItemID="[ORDERITEM]"/>"""
reshop_XML = """<Reshop> <OrderServicing> <Delete> [ORDERITEMS] </Delete> </OrderServicing> </Reshop>"""
