/*************************************************************************
 * 	Ejemplo de cómo empezar una librería	
 */

 class Lib_XXXXXX {

	def public testRunner, context, logger

	def Lib_XXXXXX ( whichTestRunner, whichContext, whichLogger ){
		this.logger	 = whichLogger
		this.context	 = whichContext
		this.testRunner = whichTestRunner
	}

	def write(message) {
		logger.info message
	}
}


final objName	= "cntx_XXX"

context.setProperty( objName, new Lib_XXXXXX( testRunner, context, log)



/*************************************************************************
 * 	Ejemplo de cómo realizar la llamada desde otro proyecto a alguno
 * 	de los pasos de esta librería.
 */	


def TestSteps			= testRunner.testCase.testSuite.project.getWorkspace().getProjectByName('Library').getTestSuiteByName('Libraries').getTestCaseByName('Library vXX')
def Lib_xxxxx			= TestSteps.getTestStepByName('Lib_XXXXXX')

log.info Lib_xxxxx.name
Lib_xxxxx.run( testRunner, context)

final ss = context.cntx_XXXX

frase = ss.write("prueba")
return "ok"
